        LOGIN(2000),
        LOGINOUT(2001),
        RECONNECT(1020), // app client reconnect
        SUCCESS_AWARE(4000), // notify client the operation is apply successful.
        INTERNAL_ERROR(4001), // middleware error
        DATA_ERROR(4002); // json data parse error

        public int value;

        Type(int value){
            this.value = value;
        }

        public int value() {
            return value;
        }

